import java.awt.*;import java.awt.event.*;import java.net.URL;import java.sql.*;class JDBC_GUI extends Frame{		static TextField queryArea;		static Button startQuery;		static TextArea resultArea;		static String[] TabName =		{ "Name", "Telephone", "Home", "Work", "Organization" };		public JDBC_GUI()		{				super("A JDBC Sample use JDBC-ODBC Bridge");				setLayout(new FlowLayout());				queryArea = new TextField("請於此處姓名查詢", 25);				startQuery = new Button("確定");				resultArea = new TextArea(15, 50);				add(queryArea);				add(startQuery);				add(resultArea);				setSize(390, 330);				show();				resultArea.append("等待指令中......\n");				resultArea.append("================\n");				addWindowListener(new WindowAdapter()				{						public void windowClosing(WindowEvent e)						{								dispose();								System.exit(0);						}				});				startQuery.addActionListener(new ActionListener()				{						public void actionPerformed(ActionEvent e)						{								try								{										Class.forName("sun.jdbc.odbc.JdbcOdbcDriver");										Connection myCon = DriverManager.getConnection("jdbc:odbc:JavaTest", "javauser",														"justjava");										String theQuery = "SELECT * FROM book WHERE Name=" + "'" + queryArea.getText()														+ "'";										Statement MyStmt = myCon.createStatement();										ResultSet myResults = MyStmt.executeQuery(theQuery);										displayResults(myResults);										myResults.close();										MyStmt.close();										myCon.close();								} catch (Exception exc)								{										resultArea.append("Can't not found data!!\n");								}						}				});		}		static void displayResults(ResultSet results) throws SQLException		{				ResultSetMetaData resultsMetaData = results.getMetaData();				int cols = resultsMetaData.getColumnCount();				resultArea.append("================\n");				while (results.next())				{						for (int i = 1; i <= cols; i++)						{								if (i > 1)										resultArea.append("\n");								resultArea.append(TabName[i - 1] + ":" + results.getString(i));						}						resultArea.append("\n");				}				resultArea.append("================\n查詢完成....\n");		}		public static void main(String argv[])		{				JDBC_GUI myJDBCTest = new JDBC_GUI();		}}